let headerElem=document.getElementById("siteHeader"),stickyPosition=headerElem.offsetTop;function makeStickyHeader(){window.pageYOffset>stickyPosition?headerElem.classList.add("header-sticky"):headerElem.classList.remove("header-sticky")}window.onscroll=function(){makeStickyHeader()};const breakPoints={xSmall:576,small:768,medium:992,large:1280,xLarge:1490,xxLarge:1920,xxxLarge:2560};function isMobile(){return window.innerWidth<breakPoints.xSmall}const menuToggle=$(".js-menu-toggle"),menuClose=$(".js-menu-close"),mobileMenu=$(".js-mobile-menu");$(document).ready(function(){menuToggle.on("click",function(){mobileMenu.is(":visible")||mobileMenu.fadeIn("fast")}),menuClose.on("click",function(){mobileMenu.is(":visible")&&mobileMenu.fadeOut("fast")})}),isMobile()&&new Glider(document.querySelector(".js-card-carousel"),{slidesToShow:1,draggable:!0,dots:".dots"});